import React from "react";
import "./app.css";

class DisplayCards extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  };

  componentDidMount() {
    this.fetchData(`https://api.spacexdata.com/v3/rockets?limit=4`);
  }

  fetchData = (apiToFetch) => {
    fetch(apiToFetch)
    .then(response => response.json())
    .then((data) => {
      console.log('Success:', data);
      this.setState({
        data: data,
      });
    })
    .catch((error) => {
      console.log("Error:", error)
    });
  };


  render() {
    return(
      <div>
       <section>
          {this.state.data.map(
            (rocketOrDragon) => {
              return(
                <section key={rocketOrDragon.id}>
                  <p> Name: {rocketOrDragon.rocket_name} </p> 
                  <p> Description </p>
                  <p> {rocketOrDragon.description} </p>
                  <p> Active? </p>
                  <p> {rocketOrDragon.active} </p>
                </section>
              )
            })
          }
        </section>
      </div>
    )
  }

}

export default DisplayCards;
