import React from 'react';
import './app.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  };

  componentDidMount() {
    this.fetchData(`https://api.spacexdata.com/v3`);
  }

  fetchData = (api) => {
    fetch(api)
    .then(response => response.json())
    .then((spaceXdata) => {
      this.setState({
        data: spaceXdata,
      });
    });
  };


  render() {
    let apiData = this.state.data;

    return (
      <div className="App">
        <header className="App-header">
          <h1>
            SpaceX
          </h1>
        </header>

        <div>
          { apiData.map((data) => {
              return(
                <section key={data.id}>
                  <p> Name </p> 
                  <p> {data.name}  </p>
                  <p> Description </p>
                  <p> {data.description} </p>
                  <p> Active? </p>
                  <p> {data.active} </p>
                </section>
              )
            })
          }
        </div>


      </div>
    );
  }

}

export default App;
